mapper.insert:
INSERT INTO audio_frequency_analysis (
analysis_result_id, frequency_band, anomaly_score, is_suspicious, created_at
) VALUES (
#{analysisResultId}, #{frequencyBand}, #{anomalyScore}, #{isSuspicious}, NOW()
)

mapper.select:
SELECT * FROM audio_frequency_analysis
WHERE analysis_result_id = #{analysisResultId}
ORDER BY frequency_band ASC
