mapper.insert:
INSERT INTO audio_time_segment_analysis (
analysis_result_id, start_time, end_time, risk_level, risk_score, reason, indicators, created_at
) VALUES (
#{analysisResultId}, #{startTime}, #{endTime}, #{riskLevel}, #{riskScore}, #{reason},
#{indicators, typeHandler=fakehunters.backend.global.mybatis.JsonNodeTypeHandler}::jsonb, NOW()
)

mapper.select:
SELECT * FROM audio_time_segment_analysis
WHERE analysis_result_id = #{analysisResultId}
ORDER BY start_time ASC
